mapper.select:
SELECT COALESCE(SUM(file_size), 0)
FROM file
WHERE user_id = #{userId}

    AND status = #{status}

SELECT 
FROM file
WHERE user_id = #{userId} AND status = 2

    AND file_id > #{lastFileId}

ORDER BY file_id ASC
LIMIT #{pageSize}

mapper.update:
UPDATE file
SET status = 0
WHERE file_id = #{fileId}

mapper.select:
SELECT storage_id
FROM file
WHERE file_id = #{fileId}

SELECT DISTINCT storage_id
FROM file
WHERE user_id = #{userId}

    AND status = #{status}

AND storage_id > 0
