mapper.select:
SELECT 
FROM user
WHERE user_id = #{userId}

    AND status = #{status}

SELECT 
FROM user
WHERE username = #{username}

    AND status = #{status}

mapper.update:
UPDATE user
SET used_quota = #{usedQuota}
WHERE user_id = #{userId}

UPDATE user
SET used_quota = used_quota + #{fileSize}
WHERE user_id = #{userId}

UPDATE user
SET used_quota = used_quota - #{fileSize}
WHERE user_id = #{userId}

UPDATE user
SET status = #{status}
WHERE user_id = #{userId}
